mapper.select:
select 

from check_item
where hosnum = #{hosnum,jdbcType=VARCHAR}
  and item_id = #{itemId,jdbcType=VARCHAR}

mapper.delete:
delete from check_item
where hosnum = #{hosnum,jdbcType=VARCHAR}
  and item_id = #{itemId,jdbcType=VARCHAR}

mapper.select:
select ci.*
from check_item ci

  join work_group_detail wgd on ci.item_id = wgd.item_id
  join work_group wg on wg.work_group_id = wgd.work_group_id and wg.work_group_id = #{workGroupId}


  join check_item_group_detail cigd on ci.item_id = cigd.item_id
  join check_item_group cig on cig.group_id = cigd.item_group_id and cig.group_id = #{checkItemGroupId}

where ci.hosnum = #{hosNum}

  and (ci.english_abbreviation LIKE '%' #{checkItem} '%' OR ci.item_name LIKE '%' #{checkItem} '%' OR ci.item_id LIKE '%' #{checkItem} '%')

mapper.delete:
DELETE FROM check_item
WHERE hosnum = #{hosNum} AND item_id IN

  #{item}

mapper.select:
select item_name
from check_item
where hosnum = #{hosNum}

select IF(max(cast(item_id as unsigned int)) + 1 = NULL, '100000', max(cast(item_id as unsigned int))  + 1)
from check_item
where hosnum = #{hosNum}
