mapper.select:
select 

from check_item_group
where hosnum = #{hosnum,jdbcType=VARCHAR}
  and group_id = #{groupId,jdbcType=VARCHAR}

mapper.delete:
delete from check_item_group
where hosnum = #{hosnum,jdbcType=VARCHAR}
  and group_id = #{groupId,jdbcType=VARCHAR}

mapper.update:
update check_item_group
set group_name = #{groupName,jdbcType=VARCHAR},
  work_group_id = #{workGroupId,jdbcType=VARCHAR},
  sampling_site = #{samplingSite,jdbcType=VARCHAR},
  sample_type = #{sampleType,jdbcType=VARCHAR},
  order_number = #{orderNumber,jdbcType=INTEGER},
  trace_log = #{traceLog,jdbcType=TIMESTAMP}
where hosnum = #{hosnum,jdbcType=VARCHAR}
  and group_id = #{groupId,jdbcType=VARCHAR}

mapper.select:
select distinct group_id,group_name
from check_item_group
where hosnum = #{hosNum} and work_group_id = #{workGroupId}

select cig.*
from check_item_group cig

  join work_group wg on cig.work_group_id = wg.work_group_id and cig.work_group_id = #{workGroupId}

where cig.hosnum = #{hosNum}

  and (cig.group_id LIKE '%' #{checkItemGroup} '%' OR cig.group_name LIKE '%' #{checkItemGroup} '%')

mapper.delete:
delete from check_item_group
where hosnum = #{hosNum} and group_id in

  #{item}

mapper.select:
select distinct group_name
from check_item_group
where hosnum = #{hosNum}

 select IF(max(cast(group_id as unsigned int)) + 1 = NULL, '1000', max(cast(group_id as unsigned int))  + 1)
from check_item_group
where hosnum = #{hosNum}

select distinct sample_type
from check_item_group
where hosnum = #{hosNum}
