mapper.select:
select 

from check_application_form
where hosnum = #{hosnum,jdbcType=VARCHAR}
  and item_id = #{itemId,jdbcType=VARCHAR}

mapper.delete:
delete from check_application_form
where hosnum = #{hosnum,jdbcType=VARCHAR}
  and item_id = #{itemId,jdbcType=VARCHAR}

mapper.select:
select *
from check_application_form
where hosnum = #{hosNum} and patient_id in

  #{item}


  and billing_time >= #{startDate}


  and billing_time <= #{endDate}
